import { React } from 'shim-react';
import { CodeStatus } from '../UiQrCode';
export type ReducerType = 'change' | 'changeStatus' | 'changeDesc';
export type RootState = {
    /**
     * 状态
     */
    status: CodeStatus;
    /**
     * 底部描述
     */
    /**
     * 当前二维码 URL
     */
    src?: string;
    /**
     * 二维码随机值
     */
    random?: string;
    /**
     * 用于与浙里办交换的认证码
     */
    authCode?: string;
    /**
     * 浙里办的认证地址
     */
    authUrl?: string;
};
/**
 * QrCode 准备阶段 Hook
 */
declare const usePreQrCode: () => {
    state: RootState;
    dispatch: React.Dispatch<{
        type: ReducerType;
        payload: Partial<RootState>;
    }>;
};
export { usePreQrCode };
